import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import { useTheme } from '../context/ThemeContext';
import { KeyIcon } from '@heroicons/react/24/outline';

const Settings = () => {
  const [apiKey, setApiKey] = useState('');
  const [keyStatus, setKeyStatus] = useState({
    hasCustomKey: false,
    hasSystemKey: false,
    isUsingCustomKey: false,
    useCustomKey: false,
    message: ''
  });
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const { token } = useAuth();
  const { isDarkMode } = useTheme();

  useEffect(() => {
    fetchApiKeyStatus();
  }, []);

  const fetchApiKeyStatus = async () => {
    try {
      const response = await axios.get('http://localhost:3000/settings/api-key', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setKeyStatus(response.data);
      if (response.data.hasCustomKey) {
        setApiKey('••••••••••••••••••••••'); // Mask for the key
      }
      setLoading(false);
    } catch (err) {
      console.error('Error fetching API key:', err);
      setError('Erro ao carregar configurações');
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSaving(true);
    setError('');
    setSuccess('');

    try {
      await axios.put('http://localhost:3000/settings/api-key', 
        { 
          apiKey,
          useCustomKey: keyStatus.useCustomKey 
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setSuccess('Configurações salvas com sucesso!');
      fetchApiKeyStatus(); // Refresh status
      setTimeout(() => setSuccess(''), 3000);
    } catch (err) {
      console.error('Error saving API key:', err);
      setError('Erro ao salvar configurações');
    } finally {
      setSaving(false);
    }
  };

  const handleToggleCustomKey = () => {
    setKeyStatus(prev => ({
      ...prev,
      useCustomKey: !prev.useCustomKey
    }));
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-600"></div>
      </div>
    );
  }

  return (
    <div className="max-w-2xl mx-auto mt-8">
      <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-8">Configurações</h2>
      
      <div className="bg-white dark:bg-gray-800 shadow rounded-lg p-6">
        <div className="mb-6">
          <div className={`p-4 rounded-md ${
            keyStatus.hasSystemKey || keyStatus.hasCustomKey ? 
              'bg-green-50 dark:bg-green-900/50 text-green-700 dark:text-green-300' : 
              'bg-yellow-50 dark:bg-yellow-900/50 text-yellow-700 dark:text-yellow-300'
          }`}>
            <p>{keyStatus.message}</p>
          </div>
        </div>

        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <div className="flex items-center justify-between mb-4">
              <label htmlFor="useCustomKey" className="text-sm font-medium text-gray-700 dark:text-gray-300">
                Usar chave API personalizada
              </label>
              <button
                type="button"
                onClick={handleToggleCustomKey}
                className={`${
                  keyStatus.useCustomKey ? 'bg-indigo-600' : 'bg-gray-200'
                } relative inline-flex h-6 w-11 flex-shrink-0 cursor-pointer rounded-full border-2 border-transparent transition-colors duration-200 ease-in-out focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2`}
              >
                <span
                  aria-hidden="true"
                  className={`${
                    keyStatus.useCustomKey ? 'translate-x-5' : 'translate-x-0'
                  } pointer-events-none inline-block h-5 w-5 transform rounded-full bg-white shadow ring-0 transition duration-200 ease-in-out`}
                />
              </button>
            </div>

            <div className={keyStatus.useCustomKey ? 'opacity-100' : 'opacity-50 pointer-events-none'}>
              <label htmlFor="apiKey" className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                Sua Chave API OpenAI
              </label>
              <div className="relative rounded-md shadow-sm">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <KeyIcon className="h-5 w-5 text-gray-400" aria-hidden="true" />
                </div>
                <input
                  type="password"
                  id="apiKey"
                  value={apiKey}
                  onChange={(e) => setApiKey(e.target.value)}
                  className="block w-full pl-10 pr-12 py-3 border border-gray-300 dark:border-gray-600 rounded-md shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm dark:bg-gray-700 dark:text-white"
                  placeholder="sk-..."
                  disabled={!keyStatus.useCustomKey}
                />
              </div>
              <p className="mt-2 text-sm text-gray-500 dark:text-gray-400">
                Configure sua própria chave API da OpenAI. Se desativada, será usada a chave do sistema.
              </p>
            </div>
          </div>

          {error && (
            <div className="text-sm text-red-600 dark:text-red-400 bg-red-50 dark:bg-red-900/50 rounded-md p-3">
              {error}
            </div>
          )}

          {success && (
            <div className="text-sm text-green-600 dark:text-green-400 bg-green-50 dark:bg-green-900/50 rounded-md p-3">
              {success}
            </div>
          )}

          <div>
            <button
              type="submit"
              disabled={saving}
              className="w-full flex justify-center py-3 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50"
            >
              {saving ? 'Salvando...' : 'Salvar Configurações'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Settings;
