import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { Link, useNavigate } from 'react-router-dom';
import { HomeIcon, DocumentTextIcon, ArrowLeftOnRectangleIcon } from '@heroicons/react/24/outline';
import NewTranscription from '../components/NewTranscription';
import SavedTranscriptions from '../components/SavedTranscriptions';

const Dashboard = () => {
  const { logout } = useAuth();
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('new');

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <div className="w-64 bg-white shadow-lg">
        <div className="flex flex-col h-full">
          <div className="flex items-center justify-center h-16 bg-indigo-600">
            <h1 className="text-white text-xl font-bold">Transkipta</h1>
          </div>
          <nav className="flex-1 px-2 py-4 space-y-1">
            <button
              onClick={() => setActiveTab('new')}
              className={`flex items-center px-4 py-2 text-sm font-medium rounded-md w-full ${
                activeTab === 'new'
                  ? 'bg-indigo-100 text-indigo-700'
                  : 'text-gray-600 hover:bg-gray-50'
              }`}
            >
              <HomeIcon className="mr-3 h-5 w-5" />
              Nova Transcrição
            </button>
            <button
              onClick={() => setActiveTab('saved')}
              className={`flex items-center px-4 py-2 text-sm font-medium rounded-md w-full ${
                activeTab === 'saved'
                  ? 'bg-indigo-100 text-indigo-700'
                  : 'text-gray-600 hover:bg-gray-50'
              }`}
            >
              <DocumentTextIcon className="mr-3 h-5 w-5" />
              Transcrições Salvas
            </button>
          </nav>
          <div className="p-4">
            <button
              onClick={handleLogout}
              className="flex items-center px-4 py-2 text-sm font-medium text-gray-600 hover:bg-gray-50 rounded-md w-full"
            >
              <ArrowLeftOnRectangleIcon className="mr-3 h-5 w-5" />
              Sair
            </button>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 overflow-auto">
        <div className="p-6">
          {activeTab === 'new' && <NewTranscription />}
          {activeTab === 'saved' && <SavedTranscriptions />}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
