import React, { useState } from 'react';
import { 
  Box, 
  Typography, 
  TextField, 
  Button, 
  Paper, 
  CircularProgress,
  Alert,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Grid,
  FormGroup,
  FormControlLabel,
  Checkbox
} from '@mui/material';
import { Download as DownloadIcon } from '@mui/icons-material';
import { useAuth } from '../context/AuthContext';

const Download = () => {
  const [url, setUrl] = useState('');
  const [platform, setPlatform] = useState('youtube');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const { token } = useAuth();

  // Opções de qualidade
  const [quality, setQuality] = useState('best');
  const [format, setFormat] = useState('mp4');
  const [audioOnly, setAudioOnly] = useState(false);

  const handleDownload = async () => {
    if (!url) {
      setError('Por favor, insira a URL do vídeo');
      return;
    }

    setLoading(true);
    setError('');
    setSuccess('');

    try {
      const response = await fetch('http://localhost:3000/download', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ 
          url, 
          platform,
          quality,
          format,
          audioOnly
        })
      });

      if (!response.ok) {
        throw new Error('Erro ao baixar o arquivo');
      }

      // Obter o blob do arquivo
      const blob = await response.blob();
      
      // Criar URL para download
      const downloadUrl = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = downloadUrl;
      
      // Definir nome do arquivo
      const timestamp = new Date().getTime();
      const extension = audioOnly ? 'mp3' : format;
      a.download = `${platform}_${timestamp}.${extension}`;
      
      // Iniciar download
      document.body.appendChild(a);
      a.click();
      
      // Limpar
      window.URL.revokeObjectURL(downloadUrl);
      document.body.removeChild(a);

      setSuccess('Download concluído com sucesso!');
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const getQualityOptions = () => {
    switch (platform) {
      case 'youtube':
        return [
          { value: 'best', label: 'Melhor qualidade' },
          { value: '1080p', label: '1080p' },
          { value: '720p', label: '720p' },
          { value: '480p', label: '480p' },
          { value: '360p', label: '360p' }
        ];
      case 'instagram':
        return [
          { value: 'best', label: 'Melhor qualidade' },
          { value: 'high', label: 'Alta qualidade' },
          { value: 'medium', label: 'Qualidade média' }
        ];
      case 'tiktok':
        return [
          { value: 'best', label: 'Melhor qualidade' },
          { value: 'high', label: 'Alta qualidade' }
        ];
      default:
        return [{ value: 'best', label: 'Melhor qualidade' }];
    }
  };

  const getFormatOptions = () => {
    if (audioOnly) {
      return [
        { value: 'mp3', label: 'MP3' },
        { value: 'm4a', label: 'M4A' }
      ];
    }
    return [
      { value: 'mp4', label: 'MP4' },
      { value: 'webm', label: 'WebM' },
      { value: 'mkv', label: 'MKV' }
    ];
  };

  return (
    <Box sx={{ maxWidth: 800, mx: 'auto', p: 3 }}>
      <Paper elevation={3} sx={{ p: 3 }}>
        <Typography variant="h5" gutterBottom>
          Download de Vídeos
        </Typography>

        <Grid container spacing={3}>
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="URL do Vídeo"
              value={url}
              onChange={(e) => setUrl(e.target.value)}
              variant="outlined"
            />
          </Grid>

          <Grid item xs={12} md={6}>
            <FormControl fullWidth>
              <InputLabel>Plataforma</InputLabel>
              <Select
                value={platform}
                onChange={(e) => setPlatform(e.target.value)}
                label="Plataforma"
              >
                <MenuItem value="youtube">YouTube</MenuItem>
                <MenuItem value="instagram">Instagram</MenuItem>
                <MenuItem value="tiktok">TikTok</MenuItem>
              </Select>
            </FormControl>
          </Grid>

          <Grid item xs={12} md={6}>
            <FormControl fullWidth>
              <InputLabel>Qualidade</InputLabel>
              <Select
                value={quality}
                onChange={(e) => setQuality(e.target.value)}
                label="Qualidade"
              >
                {getQualityOptions().map((option) => (
                  <MenuItem key={option.value} value={option.value}>
                    {option.label}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>

          <Grid item xs={12} md={6}>
            <FormControl fullWidth>
              <InputLabel>Formato</InputLabel>
              <Select
                value={format}
                onChange={(e) => setFormat(e.target.value)}
                label="Formato"
              >
                {getFormatOptions().map((option) => (
                  <MenuItem key={option.value} value={option.value}>
                    {option.label}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>

          <Grid item xs={12}>
            <FormGroup>
              <FormControlLabel
                control={
                  <Checkbox
                    checked={audioOnly}
                    onChange={(e) => {
                      setAudioOnly(e.target.checked);
                      setFormat(e.target.checked ? 'mp3' : 'mp4');
                    }}
                  />
                }
                label="Apenas áudio"
              />
            </FormGroup>
          </Grid>

          <Grid item xs={12}>
            <Button
              fullWidth
              variant="contained"
              color="primary"
              onClick={handleDownload}
              disabled={loading}
              startIcon={loading ? <CircularProgress size={20} /> : <DownloadIcon />}
            >
              {loading ? 'Baixando...' : 'Download'}
            </Button>
          </Grid>

          {error && (
            <Grid item xs={12}>
              <Alert severity="error">{error}</Alert>
            </Grid>
          )}

          {success && (
            <Grid item xs={12}>
              <Alert severity="success">{success}</Alert>
            </Grid>
          )}
        </Grid>
      </Paper>
    </Box>
  );
};

export default Download;
