import React, { useState } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import { DocumentDuplicateIcon } from '@heroicons/react/24/outline';

const NewTranscription = () => {
  const [url, setUrl] = useState('');
  const [title, setTitle] = useState('');
  const [type, setType] = useState('youtube');
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [result, setResult] = useState(null);
  const { token } = useAuth();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setResult(null);

    try {
      const formData = new FormData();
      formData.append('url', url);
      formData.append('type', type);
      formData.append('title', title);
      if (file) {
        formData.append('file', file);
      }

      const response = await axios.post(
        'http://localhost:3000/transcribe',
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'multipart/form-data',
          },
        }
      );

      setResult(response.data);
      setUrl('');
      setTitle('');
      setFile(null);
    } catch (err) {
      console.error('Error:', err);
      setError(err.response?.data?.error || 'Erro ao processar a transcrição');
    } finally {
      setLoading(false);
    }
  };

  const copyToClipboard = () => {
    navigator.clipboard.writeText(result.content);
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files?.[0];
    if (selectedFile) {
      setFile(selectedFile);
    }
  };

  const handleTypeChange = (e) => {
    setType(e.target.value);
    setFile(null);
    setUrl('');
  };

  return (
    <div className="max-w-2xl mx-auto">
      <h2 className="text-2xl font-bold text-gray-900 mb-6">Nova Transcrição</h2>
      
      <form onSubmit={handleSubmit} className="space-y-6">
        <div>
          <label htmlFor="title" className="block text-sm font-medium text-gray-700">
            Título da Transcrição
          </label>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
            placeholder="Digite um título para a transcrição"
            required
          />
        </div>

        <div>
          <label htmlFor="type" className="block text-sm font-medium text-gray-700">
            Tipo de Conteúdo
          </label>
          <select
            id="type"
            value={type}
            onChange={handleTypeChange}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
          >
            <option value="youtube">YouTube</option>
            <option value="instagram">Instagram</option>
            <option value="video">Vídeo</option>
            <option value="audio">Áudio</option>
          </select>
        </div>

        {(type === 'youtube' || type === 'instagram') ? (
          <div>
            <label htmlFor="url" className="block text-sm font-medium text-gray-700">
              URL ou Link
            </label>
            <input
              type="text"
              id="url"
              value={url}
              onChange={(e) => setUrl(e.target.value)}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
              placeholder="Cole o link aqui"
              required
            />
          </div>
        ) : (
          <div>
            <label htmlFor="file" className="block text-sm font-medium text-gray-700">
              {type === 'video' ? 'Selecione um vídeo' : 'Selecione um áudio'}
            </label>
            <input
              type="file"
              id="file"
              onChange={handleFileChange}
              accept={type === 'video' ? 'video/*' : 'audio/*'}
              className="mt-1 block w-full text-sm text-gray-500
                file:mr-4 file:py-2 file:px-4
                file:rounded-md file:border-0
                file:text-sm file:font-semibold
                file:bg-indigo-50 file:text-indigo-700
                hover:file:bg-indigo-100"
              required
            />
          </div>
        )}

        {error && (
          <div className="text-red-500 text-sm">{error}</div>
        )}

        <button
          type="submit"
          disabled={loading}
          className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50"
        >
          {loading ? 'Processando...' : 'Transcrever'}
        </button>
      </form>

      {result && (
        <div className="mt-8 bg-white shadow rounded-lg p-6">
          <div className="flex justify-between items-start mb-4">
            <h3 className="text-lg font-medium text-gray-900">{result.title}</h3>
            <button
              onClick={copyToClipboard}
              className="inline-flex items-center px-3 py-1 border border-transparent text-sm font-medium rounded-md text-indigo-700 bg-indigo-100 hover:bg-indigo-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
            >
              <DocumentDuplicateIcon className="h-4 w-4 mr-2" />
              Copiar
            </button>
          </div>
          <div className="prose max-w-none max-h-96 overflow-y-auto">
            <p className="whitespace-pre-wrap">{result.content}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default NewTranscription;
