import React, { useState } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import { useTheme } from '../context/ThemeContext';
import { 
  DocumentDuplicateIcon,
  VideoCameraIcon,
  MusicalNoteIcon
} from '@heroicons/react/24/outline';

const sourceTypes = [
  { 
    id: 'youtube', 
    name: 'YouTube',
    icon: (
      <svg className="w-8 h-8" viewBox="0 0 24 24" fill="currentColor">
        <path d="M23.498 6.186a3.016 3.016 0 0 0-2.122-2.136C19.505 3.545 12 3.545 12 3.545s-7.505 0-9.377.505A3.017 3.017 0 0 0 .502 6.186C0 8.07 0 12 0 12s0 3.93.502 5.814a3.016 3.016 0 0 0 2.122 2.136c1.871.505 9.376.505 9.376.505s7.505 0 9.377-.505a3.015 3.015 0 0 0 2.122-2.136C24 15.93 24 12 24 12s0-3.93-.502-5.814zM9.545 15.568V8.432L15.818 12l-6.273 3.568z"/>
      </svg>
    ),
    placeholder: 'Cole o link do vídeo do YouTube',
    color: 'text-red-600 dark:text-red-500',
    bgColor: 'bg-red-50 dark:bg-red-900/20',
    borderColor: 'border-red-200 dark:border-red-800'
  },
  { 
    id: 'instagram', 
    name: 'Instagram',
    icon: (
      <svg className="w-8 h-8" viewBox="0 0 24 24" fill="currentColor">
        <path d="M12 0C8.74 0 8.333.015 7.053.072 5.775.132 4.905.333 4.14.63c-.789.306-1.459.717-2.126 1.384S.935 3.35.63 4.14C.333 4.905.131 5.775.072 7.053.012 8.333 0 8.74 0 12s.015 3.667.072 4.947c.06 1.277.261 2.148.558 2.913.306.788.717 1.459 1.384 2.126.667.666 1.336 1.079 2.126 1.384.766.296 1.636.499 2.913.558C8.333 23.988 8.74 24 12 24s3.667-.015 4.947-.072c1.277-.06 2.148-.262 2.913-.558.788-.306 1.459-.718 2.126-1.384.666-.667 1.079-1.335 1.384-2.126.296-.765.499-1.636.558-2.913.06-1.28.072-1.687.072-4.947s-.015-3.667-.072-4.947c-.06-1.277-.262-2.149-.558-2.913-.306-.789-.718-1.459-1.384-2.126C21.319 1.347 20.651.935 19.86.63c-.765-.297-1.636-.499-2.913-.558C15.667.012 15.26 0 12 0zm0 2.16c3.203 0 3.585.016 4.85.071 1.17.055 1.805.249 2.227.415.562.217.96.477 1.382.896.419.42.679.819.896 1.381.164.422.36 1.057.413 2.227.057 1.266.07 1.646.07 4.85s-.015 3.585-.074 4.85c-.061 1.17-.256 1.805-.421 2.227-.224.562-.479.96-.899 1.382-.419.419-.824.679-1.38.896-.42.164-1.065.36-2.235.413-1.274.057-1.649.07-4.859.07-3.211 0-3.586-.015-4.859-.074-1.171-.061-1.816-.256-2.236-.421-.569-.224-.96-.479-1.379-.899-.421-.419-.69-.824-.9-1.38-.165-.42-.359-1.065-.42-2.235-.045-1.26-.061-1.649-.061-4.844 0-3.196.016-3.586.061-4.861.061-1.17.255-1.814.42-2.234.21-.57.479-.96.9-1.381.419-.419.81-.689 1.379-.898.42-.166 1.051-.361 2.221-.421 1.275-.045 1.65-.06 4.859-.06l.045.03zm0 3.678c-3.405 0-6.162 2.76-6.162 6.162 0 3.405 2.76 6.162 6.162 6.162 3.405 0 6.162-2.76 6.162-6.162 0-3.405-2.76-6.162-6.162-6.162zM12 16c-2.21 0-4-1.79-4-4s1.79-4 4-4 4 1.79 4 4-1.79 4-4 4zm7.846-10.405c0 .795-.646 1.44-1.44 1.44-.795 0-1.44-.646-1.44-1.44 0-.794.646-1.439 1.44-1.439.793-.001 1.44.645 1.44 1.439z"/>
      </svg>
    ),
    placeholder: 'Cole o link do vídeo do Instagram',
    color: 'text-pink-600 dark:text-pink-500',
    bgColor: 'bg-pink-50 dark:bg-pink-900/20',
    borderColor: 'border-pink-200 dark:border-pink-800'
  },
  { 
    id: 'tiktok', 
    name: 'TikTok',
    icon: (
      <svg className="w-8 h-8" viewBox="0 0 24 24" fill="currentColor">
        <path d="M19.59 6.69a4.83 4.83 0 0 1-3.77-4.25V2h-3.45v13.67a2.89 2.89 0 0 1-5.2 1.74 2.89 2.89 0 0 1 2.31-4.64 2.93 2.93 0 0 1 .88.13V9.4a6.84 6.84 0 0 0-1-.05A6.33 6.33 0 0 0 5 20.1a6.34 6.34 0 0 0 10.86-4.43v-7a8.16 8.16 0 0 0 4.77 1.52v-3.4a4.85 4.85 0 0 1-1-.1z"/>
      </svg>
    ),
    placeholder: 'Cole o link do vídeo do TikTok',
    color: 'text-gray-900 dark:text-white',
    bgColor: 'bg-gray-50 dark:bg-gray-900/20',
    borderColor: 'border-gray-200 dark:border-gray-800'
  },
  { 
    id: 'video', 
    name: 'Arquivo de Vídeo',
    icon: <VideoCameraIcon className="w-8 h-8" />,
    placeholder: 'Selecione um arquivo de vídeo',
    color: 'text-blue-600 dark:text-blue-500',
    bgColor: 'bg-blue-50 dark:bg-blue-900/20',
    borderColor: 'border-blue-200 dark:border-blue-800'
  },
  { 
    id: 'audio', 
    name: 'Arquivo de Áudio',
    icon: <MusicalNoteIcon className="w-8 h-8" />,
    placeholder: 'Selecione um arquivo de áudio',
    color: 'text-purple-600 dark:text-purple-500',
    bgColor: 'bg-purple-50 dark:bg-purple-900/20',
    borderColor: 'border-purple-200 dark:border-purple-800'
  }
];

const NewTranscription = () => {
  const [url, setUrl] = useState('');
  const [title, setTitle] = useState('');
  const [type, setType] = useState('youtube');
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [result, setResult] = useState(null);
  const [progress, setProgress] = useState(0);
  const [status, setStatus] = useState('');
  const [outputFormat, setOutputFormat] = useState('plain');
  const { token } = useAuth();
  const { isDarkMode } = useTheme();

  const updateProgressStatus = (type, stage) => {
    const progressStages = {
      youtube: {
        start: { progress: 0, status: 'Preparando...' },
        download: { progress: 20, status: 'Baixando vídeo do YouTube...' },
        convert: { progress: 50, status: 'Convertendo vídeo para áudio...' },
        transcribe: { progress: 70, status: 'Transcrevendo áudio...' },
        complete: { progress: 100, status: 'Concluído!' }
      },
      instagram: {
        start: { progress: 0, status: 'Preparando...' },
        download: { progress: 20, status: 'Baixando vídeo do Instagram...' },
        convert: { progress: 50, status: 'Convertendo vídeo para áudio...' },
        transcribe: { progress: 70, status: 'Transcrevendo áudio...' },
        complete: { progress: 100, status: 'Concluído!' }
      },
      video: {
        start: { progress: 0, status: 'Preparando...' },
        upload: { progress: 20, status: 'Enviando vídeo...' },
        convert: { progress: 50, status: 'Convertendo vídeo para áudio...' },
        transcribe: { progress: 70, status: 'Transcrevendo áudio...' },
        complete: { progress: 100, status: 'Concluído!' }
      },
      audio: {
        start: { progress: 0, status: 'Preparando...' },
        upload: { progress: 20, status: 'Enviando áudio...' },
        transcribe: { progress: 50, status: 'Transcrevendo áudio...' },
        complete: { progress: 100, status: 'Concluído!' }
      }
    };

    const stageInfo = progressStages[type][stage];
    setProgress(stageInfo.progress);
    setStatus(stageInfo.status);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setResult(null);

    updateProgressStatus(type, 'start');

    try {
      const formData = new FormData();
      formData.append('url', url);
      formData.append('type', type);
      formData.append('title', title);
      formData.append('outputFormat', outputFormat);

      if (type === 'youtube' || type === 'instagram') {
        updateProgressStatus(type, 'download');
      } else if (file) {
        formData.append('file', file);
        updateProgressStatus(type, 'upload');
      }

      const response = await axios.post(
        'http://localhost:3000/transcribe',
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'multipart/form-data',
          },
          onUploadProgress: (progressEvent) => {
            if (type === 'video' || type === 'audio') {
              const percentCompleted = Math.round((progressEvent.loaded * 100) / progressEvent.total);
              setProgress(20 + (percentCompleted * 0.3));
            }
          },
        }
      );

      if (type === 'youtube' || type === 'instagram') {
        updateProgressStatus(type, 'convert');
        await new Promise(resolve => setTimeout(resolve, 1000));
        updateProgressStatus(type, 'transcribe');
        await new Promise(resolve => setTimeout(resolve, 1000));
      } else if (type === 'video') {
        updateProgressStatus(type, 'convert');
        await new Promise(resolve => setTimeout(resolve, 1000));
        updateProgressStatus(type, 'transcribe');
        await new Promise(resolve => setTimeout(resolve, 1000));
      } else {
        updateProgressStatus(type, 'transcribe');
        await new Promise(resolve => setTimeout(resolve, 1000));
      }

      updateProgressStatus(type, 'complete');
      setResult(response.data);
      setUrl('');
      setTitle('');
      setFile(null);
    } catch (err) {
      console.error('Error:', err);
      setError(err.response?.data?.error || 'Erro ao processar a transcrição');
    } finally {
      setLoading(false);
      setTimeout(() => {
        setProgress(0);
        setStatus('');
      }, 2000);
    }
  };

  const copyToClipboard = () => {
    navigator.clipboard.writeText(result.content);
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files?.[0];
    if (selectedFile) {
      setFile(selectedFile);
    }
  };

  const handleTypeChange = (e) => {
    setType(e.target.value);
    setFile(null);
    setUrl('');
  };

  return (
    <div className="max-w-2xl mx-auto mt-8">
      <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-8">Nova Transcrição</h2>

      <form onSubmit={handleSubmit} className="space-y-6">
        <div>
          <label htmlFor="title" className="block text-sm font-medium text-gray-700 dark:text-gray-300">
            Título da Transcrição
          </label>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className="mt-1 block w-full rounded-md border-gray-300 dark:border-gray-700 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm dark:bg-gray-800 dark:text-white px-4 py-3"
            placeholder="Digite um título para a transcrição"
            required
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-3">
            Tipo de Conteúdo
          </label>
          <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
            {sourceTypes.map((source) => (
              <button
                key={source.id}
                type="button"
                onClick={() => {
                  setType(source.id);
                  setFile(null);
                  setUrl('');
                }}
                className={`relative flex flex-col items-center justify-center p-4 rounded-lg border-2 transition-all duration-200 ${
                  type === source.id 
                    ? `${source.borderColor} ${source.color} ${source.bgColor}`
                    : 'border-gray-200 dark:border-gray-700 hover:border-gray-300 dark:hover:border-gray-600'
                }`}
              >
                <div className={`${type === source.id ? source.color : 'text-gray-400 dark:text-gray-500'}`}>
                  {source.icon}
                </div>
                <span className={`mt-2 text-sm font-medium ${
                  type === source.id 
                    ? source.color
                    : 'text-gray-900 dark:text-white'
                }`}>
                  {source.name}
                </span>
              </button>
            ))}
          </div>
        </div>

        <div>
          <label htmlFor="outputFormat" className="block text-sm font-medium text-gray-700 dark:text-gray-300">
            Formato de Saída
          </label>
          <select
            id="outputFormat"
            value={outputFormat}
            onChange={(e) => setOutputFormat(e.target.value)}
            className="mt-1 block w-full rounded-md border-gray-300 dark:border-gray-700 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm dark:bg-gray-800 dark:text-white px-4 py-3"
          >
            <option value="plain">Texto Simples</option>
            <option value="srt">SRT (Legendas)</option>
            <option value="vtt">VTT (Web Video Text Tracks)</option>
          </select>
        </div>

        {(type === 'youtube' || type === 'instagram' || type === 'tiktok') ? (
          <div>
            <label htmlFor="url" className="block text-sm font-medium text-gray-700 dark:text-gray-300">
              URL ou Link
            </label>
            <input
              type="text"
              id="url"
              value={url}
              onChange={(e) => setUrl(e.target.value)}
              className="mt-1 block w-full rounded-md border-gray-300 dark:border-gray-700 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm dark:bg-gray-800 dark:text-white px-4 py-3"
              placeholder={sourceTypes.find(s => s.id === type)?.placeholder}
              required
            />
          </div>
        ) : (
          <div>
            <label htmlFor="file" className="block text-sm font-medium text-gray-700 dark:text-gray-300">
              {type === 'video' ? 'Selecione um vídeo' : 'Selecione um áudio'}
            </label>
            <input
              type="file"
              id="file"
              onChange={handleFileChange}
              accept={type === 'video' ? 'video/*' : 'audio/*'}
              className="mt-1 block w-full text-sm text-gray-500 dark:text-gray-400
                file:mr-4 file:py-3 file:px-4
                file:rounded-md file:border-0
                file:text-sm file:font-semibold
                file:bg-indigo-50 file:text-indigo-700
                hover:file:bg-indigo-100
                dark:file:bg-indigo-900 dark:file:text-indigo-200
                dark:hover:file:bg-indigo-800"
              required
            />
          </div>
        )}

        {error && (
          <div className="text-red-500 text-sm">{error}</div>
        )}

        {loading && (
          <div className="space-y-2">
            <div className="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-2.5">
              <div
                className="bg-indigo-600 h-2.5 rounded-full transition-all duration-300"
                style={{ width: `${progress}%` }}
              ></div>
            </div>
            <p className="text-sm text-gray-500 dark:text-gray-400 text-center">{status}</p>
          </div>
        )}

        <button
          type="submit"
          disabled={loading}
          className="w-full flex justify-center py-3 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50"
        >
          {loading ? 'Processando...' : 'Transcrever'}
        </button>
      </form>

      {result && (
        <div className="mt-8 bg-white dark:bg-gray-800 shadow rounded-lg p-6">
          <div className="flex justify-between items-start mb-4">
            <h3 className="text-lg font-medium text-gray-900 dark:text-white">{result.title}</h3>
            <button
              onClick={copyToClipboard}
              className="inline-flex items-center px-3 py-1 border border-transparent text-sm font-medium rounded-md text-indigo-700 dark:text-indigo-200 bg-indigo-100 dark:bg-indigo-900 hover:bg-indigo-200 dark:hover:bg-indigo-800 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
            >
              <DocumentDuplicateIcon className="h-4 w-4 mr-2" />
              Copiar
            </button>
          </div>
          <div className="prose max-w-none max-h-96 overflow-y-auto dark:prose-invert">
            <p className="whitespace-pre-wrap text-gray-900 dark:text-gray-100">{result.content}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default NewTranscription;
