import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import { useTheme } from '../context/ThemeContext';
import { KeyIcon, TrashIcon, FunnelIcon } from '@heroicons/react/24/outline';
import { 
  Box, 
  Typography, 
  TextField, 
  Button, 
  Paper, 
  CircularProgress,
  Alert,
  Tabs,
  Tab,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  IconButton,
  Tooltip,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Switch,
  FormControlLabel,
  Grid
} from '@mui/material';
import { 
  Save as SaveIcon,
  Refresh as RefreshIcon,
  Delete as DeleteIcon,
  ContentCopy as CopyIcon,
  FilterList as FilterIcon,
  Visibility as VisibilityIcon,
  VisibilityOff as VisibilityOffIcon
} from '@mui/icons-material';

const Settings = () => {
  const [apiKey, setApiKey] = useState('');
  const [showKey, setShowKey] = useState(false);
  const [keyStatus, setKeyStatus] = useState({
    hasCustomKey: false,
    hasSystemKey: false,
    isUsingCustomKey: false,
    useCustomKey: false,
    message: ''
  });
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const { token } = useAuth();
  const { isDarkMode } = useTheme();
  const { user } = useAuth();
  const [logs, setLogs] = useState([]);
  const [filters, setFilters] = useState({
    level: '',
    startDate: '',
    endDate: ''
  });
  const [logsLoading, setLogsLoading] = useState(false);
  const [activeTab, setActiveTab] = useState(0);

  const fetchApiKeyStatus = async () => {
    try {
      const response = await axios.get('http://localhost:3000/settings/api-key', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setKeyStatus(response.data);
      if (response.data.hasCustomKey) {
        setApiKey('••••••••••••••••••••••'); // Mask for the key
      }
      setLoading(false);
    } catch (err) {
      console.error('Error fetching API key:', err);
      setError('Erro ao carregar configurações');
      setLoading(false);
    }
  };

  const fetchLogs = async () => {
    try {
      setLogsLoading(true);
      setError(null);
      const params = {
        ...filters,
        limit: 100
      };
      const response = await axios.get('http://localhost:3000/logs', {
        headers: { Authorization: `Bearer ${token}` },
        params
      });
      setLogs(response.data);
    } catch (err) {
      setError('Erro ao carregar logs');
      console.error('Error fetching logs:', err);
    } finally {
      setLogsLoading(false);
    }
  };

  useEffect(() => {
    fetchApiKeyStatus();
    fetchLogs();
  }, [token, filters]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSaving(true);
    setError('');
    setSuccess('');

    try {
      await axios.put('http://localhost:3000/settings/api-key', 
        { 
          apiKey,
          useCustomKey: keyStatus.useCustomKey 
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setSuccess('Configurações salvas com sucesso!');
      fetchApiKeyStatus(); // Refresh status
      setTimeout(() => setSuccess(''), 3000);
    } catch (err) {
      console.error('Error saving API key:', err);
      setError('Erro ao salvar configurações');
    } finally {
      setSaving(false);
    }
  };

  const handleToggleCustomKey = () => {
    setKeyStatus(prev => ({
      ...prev,
      useCustomKey: !prev.useCustomKey
    }));
  };

  const handleDeleteLogs = async () => {
    if (!window.confirm('Tem certeza que deseja excluir os logs?')) return;

    try {
      setLoading(true);
      await axios.delete('http://localhost:3000/logs', {
        headers: { Authorization: `Bearer ${token}` },
        params: {
          startDate: filters.startDate,
          endDate: filters.endDate
        }
      });
      await fetchLogs();
    } catch (err) {
      setError('Erro ao excluir logs');
      console.error('Error deleting logs:', err);
    } finally {
      setLoading(false);
    }
  };

  const getLevelColor = (level) => {
    switch (level) {
      case 'error':
        return 'text-red-500 dark:text-red-400';
      case 'warning':
        return 'text-yellow-500 dark:text-yellow-400';
      default:
        return 'text-green-500 dark:text-green-400';
    }
  };

  const handleCopyApiKey = () => {
    navigator.clipboard.writeText(apiKey);
    setSuccess('Chave da API copiada!');
    setTimeout(() => setSuccess(''), 2000);
  };

  const formatDate = (dateString) => {
    try {
      if (!dateString) return 'N/A';
      
      const date = new Date(dateString);
      if (isNaN(date.getTime())) {
        console.error('Invalid date:', dateString);
        return 'Data inválida';
      }

      return date.toLocaleString('pt-BR', {
        day: '2-digit',
        month: '2-digit',
        year: 'numeric',
        hour: '2-digit',
        minute: '2-digit',
        second: '2-digit'
      });
    } catch (error) {
      console.error('Error formatting date:', error);
      return 'Erro ao formatar data';
    }
  };

  const handleClearFilters = () => {
    setFilters({
      level: '',
      startDate: '',
      endDate: ''
    });
    fetchLogs();
  };

  const formatApiKey = (key) => {
    if (!key) return '';
    if (key === '••••••••••••••••••••••') return key;
    
    if (showKey) {
      return key;
    }
    
    const visibleChars = 4;
    const start = key.substring(0, visibleChars);
    const end = key.substring(key.length - visibleChars);
    const middle = '••••••••••••••••••••••';
    
    return `${start}${middle}${end}`;
  };

  const handleToggleVisibility = () => {
    setShowKey(!showKey);
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-600"></div>
      </div>
    );
  }

  return (
    <Box sx={{ maxWidth: 1200, mx: 'auto', p: 3 }}>
      <Paper 
        elevation={3} 
        sx={{ 
          p: 3,
          bgcolor: isDarkMode ? '#1a1a1a' : 'background.default',
          color: isDarkMode ? 'text.primary' : 'text.secondary',
          '& .MuiOutlinedInput-root': {
            '& fieldset': {
              borderColor: isDarkMode ? 'rgba(255, 255, 255, 0.23)' : 'rgba(0, 0, 0, 0.23)',
            },
            '&:hover fieldset': {
              borderColor: isDarkMode ? 'rgba(255, 255, 255, 0.5)' : 'rgba(0, 0, 0, 0.5)',
            },
          },
        }}
      >
        <Typography variant="h5" gutterBottom>
          Configurações
        </Typography>

        <Tabs 
          value={activeTab} 
          onChange={(e, newValue) => setActiveTab(newValue)}
          sx={{ 
            mb: 3,
            '& .MuiTab-root': {
              color: isDarkMode ? 'text.primary' : 'text.secondary',
            },
            '& .Mui-selected': {
              color: isDarkMode ? 'primary.light' : 'primary.main',
            },
          }}
        >
          <Tab label="API Key" />
          <Tab label="Logs" />
        </Tabs>

        {activeTab === 0 && (
          <Box>
            <Typography variant="h6" gutterBottom>
              Configurações da API
            </Typography>

            <Box component="form" onSubmit={handleSubmit} sx={{ mt: 3 }}>
              <Grid container spacing={3}>
                <Grid item xs={12}>
                  <TextField
                    fullWidth
                    label="Chave da API"
                    value={apiKey}
                    onChange={(e) => setApiKey(e.target.value)}
                    type={showKey ? 'text' : 'password'}
                    variant="outlined"
                    sx={{
                      '& .MuiOutlinedInput-root': {
                        color: isDarkMode ? 'text.primary' : 'text.secondary',
                        '& fieldset': {
                          borderColor: isDarkMode ? 'rgba(255, 255, 255, 0.23)' : 'rgba(0, 0, 0, 0.23)',
                        },
                      },
                      '& .MuiInputLabel-root': {
                        color: isDarkMode ? 'text.primary' : 'text.secondary',
                      },
                    }}
                    InputProps={{
                      endAdornment: (
                        <IconButton
                          onClick={() => setShowKey(!showKey)}
                          edge="end"
                          sx={{ color: isDarkMode ? 'text.primary' : 'text.secondary' }}
                        >
                          {showKey ? <VisibilityOffIcon /> : <VisibilityIcon />}
                        </IconButton>
                      ),
                    }}
                  />
                </Grid>

                <Grid item xs={12}>
                  <FormControlLabel
                    control={
                      <Switch
                        checked={keyStatus.useCustomKey}
                        onChange={handleToggleCustomKey}
                        sx={{
                          '& .MuiSwitch-thumb': {
                            color: isDarkMode ? 'primary.light' : 'primary.main',
                          },
                          '& .MuiSwitch-track': {
                            backgroundColor: isDarkMode ? 'rgba(255, 255, 255, 0.3)' : 'rgba(0, 0, 0, 0.3)',
                          },
                        }}
                      />
                    }
                    label="Usar chave personalizada"
                    sx={{ color: isDarkMode ? 'text.primary' : 'text.secondary' }}
                  />
                </Grid>

                <Grid item xs={12}>
                  <Button
                    type="submit"
                    variant="contained"
                    color="primary"
                    disabled={saving}
                    startIcon={saving ? <CircularProgress size={20} /> : <SaveIcon />}
                    sx={{
                      bgcolor: isDarkMode ? 'primary.light' : 'primary.main',
                      '&:hover': {
                        bgcolor: isDarkMode ? 'primary.dark' : 'primary.dark',
                      },
                    }}
                  >
                    {saving ? 'Salvando...' : 'Salvar Configurações'}
                  </Button>
                </Grid>
              </Grid>
            </Box>
          </Box>
        )}

        {activeTab === 1 && (
          <Box>
            <Typography variant="h6" gutterBottom>
              Logs do Sistema
            </Typography>

            <Box sx={{ mb: 3 }}>
              <Grid container spacing={2} alignItems="center">
                <Grid item xs={12} md={3}>
                  <FormControl fullWidth>
                    <InputLabel sx={{ color: isDarkMode ? 'text.primary' : 'text.secondary' }}>
                      Nível
                    </InputLabel>
                    <Select
                      value={filters.level}
                      onChange={(e) => setFilters({ ...filters, level: e.target.value })}
                      label="Nível"
                      sx={{
                        color: isDarkMode ? 'text.primary' : 'text.secondary',
                        '& .MuiOutlinedInput-notchedOutline': {
                          borderColor: isDarkMode ? 'rgba(255, 255, 255, 0.23)' : 'rgba(0, 0, 0, 0.23)',
                        },
                      }}
                    >
                      <MenuItem value="">Todos</MenuItem>
                      <MenuItem value="info">Info</MenuItem>
                      <MenuItem value="warning">Warning</MenuItem>
                      <MenuItem value="error">Error</MenuItem>
                    </Select>
                  </FormControl>
                </Grid>

                <Grid item xs={12} md={3}>
                  <TextField
                    fullWidth
                    type="date"
                    label="Data Inicial"
                    value={filters.startDate}
                    onChange={(e) => setFilters({ ...filters, startDate: e.target.value })}
                    InputLabelProps={{ shrink: true }}
                    sx={{
                      '& .MuiOutlinedInput-root': {
                        color: isDarkMode ? 'text.primary' : 'text.secondary',
                        '& fieldset': {
                          borderColor: isDarkMode ? 'rgba(255, 255, 255, 0.23)' : 'rgba(0, 0, 0, 0.23)',
                        },
                      },
                      '& .MuiInputLabel-root': {
                        color: isDarkMode ? 'text.primary' : 'text.secondary',
                      },
                    }}
                  />
                </Grid>

                <Grid item xs={12} md={3}>
                  <TextField
                    fullWidth
                    type="date"
                    label="Data Final"
                    value={filters.endDate}
                    onChange={(e) => setFilters({ ...filters, endDate: e.target.value })}
                    InputLabelProps={{ shrink: true }}
                    sx={{
                      '& .MuiOutlinedInput-root': {
                        color: isDarkMode ? 'text.primary' : 'text.secondary',
                        '& fieldset': {
                          borderColor: isDarkMode ? 'rgba(255, 255, 255, 0.23)' : 'rgba(0, 0, 0, 0.23)',
                        },
                      },
                      '& .MuiInputLabel-root': {
                        color: isDarkMode ? 'text.primary' : 'text.secondary',
                      },
                    }}
                  />
                </Grid>

                <Grid item xs={12} md={3}>
                  <Button
                    variant="contained"
                    color="primary"
                    onClick={handleDeleteLogs}
                    startIcon={<DeleteIcon />}
                    sx={{
                      bgcolor: isDarkMode ? 'primary.light' : 'primary.main',
                      '&:hover': {
                        bgcolor: isDarkMode ? 'primary.dark' : 'primary.dark',
                      },
                    }}
                  >
                    Limpar Logs
                  </Button>
                </Grid>
              </Grid>
            </Box>

            <TableContainer component={Paper} sx={{ 
              bgcolor: isDarkMode ? '#1a1a1a' : 'background.default',
              color: isDarkMode ? 'text.primary' : 'text.secondary',
              '& .MuiTableRow-root': {
                '&:nth-of-type(odd)': {
                  backgroundColor: isDarkMode ? '#2a2a2a' : 'background.paper',
                },
              },
            }}>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell sx={{ color: isDarkMode ? 'text.primary' : 'text.secondary' }}>Data</TableCell>
                    <TableCell sx={{ color: isDarkMode ? 'text.primary' : 'text.secondary' }}>Nível</TableCell>
                    <TableCell sx={{ color: isDarkMode ? 'text.primary' : 'text.secondary' }}>Mensagem</TableCell>
                    <TableCell sx={{ color: isDarkMode ? 'text.primary' : 'text.secondary' }}>Detalhes</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {logs.map((log) => (
                    <TableRow key={log.id}>
                      <TableCell sx={{ color: isDarkMode ? 'text.primary' : 'text.secondary' }}>
                        {formatDate(log.timestamp)}
                      </TableCell>
                      <TableCell sx={{ color: getLevelColor(log.level) }}>
                        {log.level}
                      </TableCell>
                      <TableCell sx={{ color: isDarkMode ? 'text.primary' : 'text.secondary' }}>
                        {log.message}
                      </TableCell>
                      <TableCell sx={{ color: isDarkMode ? 'text.primary' : 'text.secondary' }}>
                        {JSON.stringify(log.details)}
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          </Box>
        )}

        {error && (
          <Alert severity="error" sx={{ mt: 2 }}>
            {error}
          </Alert>
        )}

        {success && (
          <Alert severity="success" sx={{ mt: 2 }}>
            {success}
          </Alert>
        )}
      </Paper>
    </Box>
  );
};

export default Settings;
