import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';

const SavedTranscriptions = () => {
  const [transcriptions, setTranscriptions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [selectedTranscription, setSelectedTranscription] = useState(null);
  const { token } = useAuth();

  useEffect(() => {
    fetchTranscriptions();
  }, []);

  const fetchTranscriptions = async () => {
    try {
      const response = await axios.get('http://localhost:3000/transcriptions', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setTranscriptions(response.data);
    } catch (err) {
      setError('Error fetching transcriptions');
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('pt-BR', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-600"></div>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto">
      <h2 className="text-2xl font-bold text-gray-900 mb-6">Transcrições Salvas</h2>

      {error && (
        <div className="text-red-500 text-sm mb-4">{error}</div>
      )}

      {transcriptions.length === 0 ? (
        <div className="text-center text-gray-500">
          Nenhuma transcrição encontrada.
        </div>
      ) : (
        <div className="grid grid-cols-1 gap-4">
          {transcriptions.map((transcription) => (
            <div
              key={transcription.id}
              className="bg-white shadow rounded-lg p-4 cursor-pointer hover:shadow-md transition-shadow"
              onClick={() => setSelectedTranscription(transcription)}
            >
              <div className="flex justify-between items-start">
                <div>
                  <h3 className="text-lg font-medium text-gray-900">
                    {transcription.title}
                  </h3>
                  <p className="text-sm text-gray-500">
                    {formatDate(transcription.created_at)}
                  </p>
                </div>
                <span className="px-2 py-1 text-xs font-semibold rounded-full bg-indigo-100 text-indigo-800">
                  {transcription.type}
                </span>
              </div>
            </div>
          ))}
        </div>
      )}

      {selectedTranscription && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4">
          <div className="bg-white rounded-lg max-w-2xl w-full p-6">
            <div className="flex justify-between items-start mb-4">
              <h3 className="text-lg font-medium text-gray-900">
                {selectedTranscription.title}
              </h3>
              <button
                onClick={() => setSelectedTranscription(null)}
                className="text-gray-400 hover:text-gray-500"
              >
                ✕
              </button>
            </div>
            <div className="prose max-w-none">
              <p className="whitespace-pre-wrap">{selectedTranscription.content}</p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default SavedTranscriptions;
