import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import { useTheme } from '../context/ThemeContext';
import { KeyIcon, TrashIcon, FunnelIcon } from '@heroicons/react/24/outline';
import { 
  Box, 
  Typography, 
  TextField, 
  Button, 
  Paper, 
  CircularProgress,
  Alert,
  Tabs,
  Tab,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  IconButton,
  Tooltip
} from '@mui/material';
import { 
  Save as SaveIcon,
  Refresh as RefreshIcon,
  Delete as DeleteIcon,
  ContentCopy as CopyIcon
} from '@mui/icons-material';

const Settings = () => {
  const [apiKey, setApiKey] = useState('');
  const [keyStatus, setKeyStatus] = useState({
    hasCustomKey: false,
    hasSystemKey: false,
    isUsingCustomKey: false,
    useCustomKey: false,
    message: ''
  });
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const { token } = useAuth();
  const { isDarkMode } = useTheme();
  const { user } = useAuth();
  const [logs, setLogs] = useState([]);
  const [filters, setFilters] = useState({
    level: '',
    startDate: '',
    endDate: ''
  });
  const [logsLoading, setLogsLoading] = useState(false);
  const [activeTab, setActiveTab] = useState(0);

  useEffect(() => {
    fetchApiKeyStatus();
    fetchLogs();
  }, []);

  const fetchApiKeyStatus = async () => {
    try {
      const response = await axios.get('http://localhost:3000/settings/api-key', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setKeyStatus(response.data);
      if (response.data.hasCustomKey) {
        setApiKey('••••••••••••••••••••••'); // Mask for the key
      }
      setLoading(false);
    } catch (err) {
      console.error('Error fetching API key:', err);
      setError('Erro ao carregar configurações');
      setLoading(false);
    }
  };

  const fetchLogs = async () => {
    try {
      setLogsLoading(true);
      setError(null);
      const params = {
        ...filters,
        limit: 100
      };
      const response = await axios.get('http://localhost:3000/logs', {
        headers: { Authorization: `Bearer ${token}` },
        params
      });
      setLogs(response.data);
    } catch (err) {
      setError('Erro ao carregar logs');
      console.error('Error fetching logs:', err);
    } finally {
      setLogsLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSaving(true);
    setError('');
    setSuccess('');

    try {
      await axios.put('http://localhost:3000/settings/api-key', 
        { 
          apiKey,
          useCustomKey: keyStatus.useCustomKey 
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setSuccess('Configurações salvas com sucesso!');
      fetchApiKeyStatus(); // Refresh status
      setTimeout(() => setSuccess(''), 3000);
    } catch (err) {
      console.error('Error saving API key:', err);
      setError('Erro ao salvar configurações');
    } finally {
      setSaving(false);
    }
  };

  const handleToggleCustomKey = () => {
    setKeyStatus(prev => ({
      ...prev,
      useCustomKey: !prev.useCustomKey
    }));
  };

  const handleDeleteLogs = async () => {
    if (!window.confirm('Tem certeza que deseja excluir os logs?')) return;

    try {
      setLoading(true);
      await axios.delete('http://localhost:3000/logs', {
        headers: { Authorization: `Bearer ${token}` },
        params: {
          startDate: filters.startDate,
          endDate: filters.endDate
        }
      });
      await fetchLogs();
    } catch (err) {
      setError('Erro ao excluir logs');
      console.error('Error deleting logs:', err);
    } finally {
      setLoading(false);
    }
  };

  const getLevelColor = (level) => {
    switch (level) {
      case 'error':
        return 'text-red-500 dark:text-red-400';
      case 'warning':
        return 'text-yellow-500 dark:text-yellow-400';
      default:
        return 'text-green-500 dark:text-green-400';
    }
  };

  const handleCopyApiKey = () => {
    navigator.clipboard.writeText(apiKey);
    setSuccess('Chave da API copiada!');
    setTimeout(() => setSuccess(''), 2000);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString('pt-BR', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit'
    });
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-600"></div>
      </div>
    );
  }

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" gutterBottom>
        Configurações
      </Typography>

      {error && (
        <Alert 
          severity="error" 
          sx={{ mb: 2 }}
        >
          {error}
        </Alert>
      )}

      {success && (
        <Alert 
          severity="success" 
          sx={{ mb: 2 }}
        >
          {success}
        </Alert>
      )}

      <Paper sx={{ p: 3, mb: 3 }}>
        <Tabs 
          value={activeTab} 
          onChange={(e, newValue) => setActiveTab(newValue)}
          sx={{ mb: 3 }}
        >
          <Tab label="API" />
          <Tab label="Logs" />
        </Tabs>

        {activeTab === 0 && (
          <Box>
            <Typography variant="h6" gutterBottom>
              Configurações da API
            </Typography>
            <Box sx={{ display: 'flex', gap: 2, mb: 2 }}>
              <TextField
                fullWidth
                label="Chave da API"
                value={apiKey}
                onChange={(e) => setApiKey(e.target.value)}
                disabled={loading}
                type="password"
              />
              <Tooltip title="Copiar chave">
                <IconButton 
                  onClick={handleCopyApiKey}
                  disabled={!apiKey}
                >
                  <CopyIcon />
                </IconButton>
              </Tooltip>
            </Box>
            <Button
              variant="contained"
              startIcon={<SaveIcon />}
              onClick={handleSubmit}
              disabled={loading}
            >
              {loading ? <CircularProgress size={24} /> : 'Salvar'}
            </Button>
          </Box>
        )}

        {activeTab === 1 && (
          <Box>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
              <Typography variant="h6">
                Logs do Sistema
              </Typography>
              <Box>
                <Tooltip title="Atualizar logs">
                  <IconButton 
                    onClick={fetchLogs}
                    disabled={logsLoading}
                    sx={{ mr: 1 }}
                  >
                    <RefreshIcon />
                  </IconButton>
                </Tooltip>
                <Tooltip title="Limpar logs">
                  <IconButton 
                    onClick={handleDeleteLogs}
                    disabled={logsLoading || logs.length === 0}
                  >
                    <DeleteIcon />
                  </IconButton>
                </Tooltip>
              </Box>
            </Box>

            <TableContainer component={Paper}>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>Data</TableCell>
                    <TableCell>Nível</TableCell>
                    <TableCell>Mensagem</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {logsLoading ? (
                    <TableRow>
                      <TableCell colSpan={3} align="center">
                        <CircularProgress />
                      </TableCell>
                    </TableRow>
                  ) : logs.length === 0 ? (
                    <TableRow>
                      <TableCell colSpan={3} align="center">
                        Nenhum log encontrado
                      </TableCell>
                    </TableRow>
                  ) : (
                    logs.map((log) => (
                      <TableRow key={log.id}>
                        <TableCell>{formatDate(log.createdAt)}</TableCell>
                        <TableCell>{log.level}</TableCell>
                        <TableCell>{log.message}</TableCell>
                      </TableRow>
                    ))
                  )}
                </TableBody>
              </Table>
            </TableContainer>
          </Box>
        )}
      </Paper>
