import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { useTheme } from '../context/ThemeContext';
import { Link, useNavigate } from 'react-router-dom';
import { HomeIcon, DocumentTextIcon, ArrowLeftOnRectangleIcon, SunIcon, MoonIcon, Cog6ToothIcon } from '@heroicons/react/24/outline';
import NewTranscription from '../components/NewTranscription';
import SavedTranscriptions from '../components/SavedTranscriptions';
import Settings from '../components/Settings';

const Dashboard = () => {
  const { logout } = useAuth();
  const { isDarkMode, toggleTheme } = useTheme();
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('new');

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <div className="flex h-screen bg-gray-50 dark:bg-gray-900">
      {/* Sidebar */}
      <div className="w-64 bg-white dark:bg-gray-800 shadow-lg">
        <div className="flex flex-col h-full">
          <div className="flex items-center justify-between h-16 bg-indigo-600 px-4">
            <h1 className="text-white text-xl font-bold">Transkipta</h1>
            <button
              onClick={toggleTheme}
              className="p-2 rounded-full text-white hover:bg-indigo-700 transition-colors duration-200"
              title={isDarkMode ? "Modo Claro" : "Modo Escuro"}
            >
              {isDarkMode ? (
                <SunIcon className="h-5 w-5" />
              ) : (
                <MoonIcon className="h-5 w-5" />
              )}
            </button>
          </div>
          <nav className="flex-1 px-2 py-4 space-y-1">
            <button
              onClick={() => setActiveTab('new')}
              className={`flex items-center px-4 py-3 text-sm font-medium rounded-md w-full ${
                activeTab === 'new'
                  ? 'bg-indigo-100 dark:bg-indigo-900 text-indigo-700 dark:text-indigo-200'
                  : 'text-gray-600 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700'
              }`}
            >
              <HomeIcon className="mr-3 h-5 w-5" />
              Nova Transcrição
            </button>
            <button
              onClick={() => setActiveTab('saved')}
              className={`flex items-center px-4 py-3 text-sm font-medium rounded-md w-full ${
                activeTab === 'saved'
                  ? 'bg-indigo-100 dark:bg-indigo-900 text-indigo-700 dark:text-indigo-200'
                  : 'text-gray-600 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700'
              }`}
            >
              <DocumentTextIcon className="mr-3 h-5 w-5" />
              Transcrições Salvas
            </button>
            <button
              onClick={() => setActiveTab('settings')}
              className={`flex items-center px-4 py-3 text-sm font-medium rounded-md w-full ${
                activeTab === 'settings'
                  ? 'bg-indigo-100 dark:bg-indigo-900 text-indigo-700 dark:text-indigo-200'
                  : 'text-gray-600 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700'
              }`}
            >
              <Cog6ToothIcon className="mr-3 h-5 w-5" />
              Configurações
            </button>
          </nav>
          <div className="p-4">
            <button
              onClick={handleLogout}
              className="flex items-center px-4 py-3 text-sm font-medium text-gray-600 dark:text-gray-300 hover:bg-gray-50 dark:hover:bg-gray-700 rounded-md w-full"
            >
              <ArrowLeftOnRectangleIcon className="mr-3 h-5 w-5" />
              Sair
            </button>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 overflow-auto">
        <div className="p-8">
          {activeTab === 'new' && <NewTranscription />}
          {activeTab === 'saved' && <SavedTranscriptions />}
          {activeTab === 'settings' && <Settings />}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
