import React, { useState } from 'react';
import { 
  Box, 
  Typography, 
  TextField, 
  Button, 
  Paper, 
  CircularProgress,
  Alert,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Grid,
  FormGroup,
  FormControlLabel,
  Checkbox
} from '@mui/material';
import { Download as DownloadIcon } from '@mui/icons-material';
import { useAuth } from '../context/AuthContext';
import { useTheme } from '../context/ThemeContext';

const Download = () => {
  const [url, setUrl] = useState('');
  const [platform, setPlatform] = useState('youtube');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const { token } = useAuth();
  const { isDarkMode } = useTheme();

  // Opções de qualidade
  const [quality, setQuality] = useState('best');
  const [format, setFormat] = useState('mp4');
  const [audioOnly, setAudioOnly] = useState(false);

  const handleDownload = async () => {
    if (!url) {
      setError('Por favor, insira a URL do vídeo');
      return;
    }

    setLoading(true);
    setError('');
    setSuccess('');

    try {
      const response = await fetch('http://localhost:3000/download', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify({ 
          url, 
          platform,
          quality,
          format,
          audioOnly
        })
      });

      if (!response.ok) {
        throw new Error('Erro ao baixar o vídeo');
      }

      // Criar um blob do arquivo baixado
      const blob = await response.blob();
      const downloadUrl = window.URL.createObjectURL(blob);
      
      // Criar um link temporário para download
      const link = document.createElement('a');
      link.href = downloadUrl;
      link.download = `${platform}_${Date.now()}.${format}`;
      document.body.appendChild(link);
      link.click();
      
      // Limpar
      document.body.removeChild(link);
      window.URL.revokeObjectURL(downloadUrl);

      setSuccess('Download concluído com sucesso!');
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const getQualityOptions = () => {
    switch (platform) {
      case 'youtube':
        return [
          { value: 'best', label: 'Melhor qualidade' },
          { value: '1080p', label: '1080p' },
          { value: '720p', label: '720p' },
          { value: '480p', label: '480p' },
          { value: '360p', label: '360p' }
        ];
      case 'instagram':
        return [
          { value: 'best', label: 'Melhor qualidade' },
          { value: 'high', label: 'Alta qualidade' },
          { value: 'medium', label: 'Qualidade média' }
        ];
      case 'tiktok':
        return [
          { value: 'best', label: 'Melhor qualidade' },
          { value: 'high', label: 'Alta qualidade' }
        ];
      default:
        return [{ value: 'best', label: 'Melhor qualidade' }];
    }
  };

  const getFormatOptions = () => {
    if (audioOnly) {
      return [{ value: 'mp3', label: 'MP3' }];
    }
    return [
      { value: 'mp4', label: 'MP4' },
      { value: 'webm', label: 'WebM' },
      { value: 'mkv', label: 'MKV' }
    ];
  };

  return (
    <Box sx={{ maxWidth: 800, mx: 'auto', p: 3 }}>
      <Paper 
        elevation={3} 
        sx={{ 
          p: 3,
          bgcolor: isDarkMode ? '#1a1a1a' : 'background.default',
          color: isDarkMode ? 'text.primary' : 'text.secondary',
          '& .MuiOutlinedInput-root': {
            '& fieldset': {
              borderColor: isDarkMode ? 'rgba(255, 255, 255, 0.23)' : 'rgba(0, 0, 0, 0.23)',
            },
            '&:hover fieldset': {
              borderColor: isDarkMode ? 'rgba(255, 255, 255, 0.5)' : 'rgba(0, 0, 0, 0.5)',
            },
          },
        }}
      >
        <Typography variant="h5" gutterBottom>
          Download de Vídeos
        </Typography>

        <Grid container spacing={3}>
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="URL do Vídeo"
              value={url}
              onChange={(e) => setUrl(e.target.value)}
              variant="outlined"
              sx={{
                '& .MuiOutlinedInput-root': {
                  color: isDarkMode ? 'text.primary' : 'text.secondary',
                },
              }}
            />
          </Grid>

          <Grid item xs={12} md={6}>
            <FormControl fullWidth>
              <InputLabel sx={{ color: isDarkMode ? 'text.primary' : 'text.secondary' }}>
                Plataforma
              </InputLabel>
              <Select
                value={platform}
                onChange={(e) => setPlatform(e.target.value)}
                label="Plataforma"
                sx={{
                  color: isDarkMode ? 'text.primary' : 'text.secondary',
                }}
              >
                <MenuItem value="youtube">YouTube</MenuItem>
                <MenuItem value="instagram">Instagram</MenuItem>
                <MenuItem value="tiktok">TikTok</MenuItem>
              </Select>
            </FormControl>
          </Grid>

          <Grid item xs={12} md={6}>
            <FormControl fullWidth>
              <InputLabel sx={{ color: isDarkMode ? 'text.primary' : 'text.secondary' }}>
                Qualidade
              </InputLabel>
              <Select
                value={quality}
                onChange={(e) => setQuality(e.target.value)}
                label="Qualidade"
                sx={{
                  color: isDarkMode ? 'text.primary' : 'text.secondary',
                }}
              >
                {getQualityOptions().map((option) => (
                  <MenuItem key={option.value} value={option.value}>
                    {option.label}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>

          <Grid item xs={12} md={6}>
            <FormControl fullWidth>
              <InputLabel sx={{ color: isDarkMode ? 'text.primary' : 'text.secondary' }}>
                Formato
              </InputLabel>
              <Select
                value={format}
                onChange={(e) => setFormat(e.target.value)}
                label="Formato"
                sx={{
                  color: isDarkMode ? 'text.primary' : 'text.secondary',
                }}
              >
                {getFormatOptions().map((option) => (
                  <MenuItem key={option.value} value={option.value}>
                    {option.label}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>

          <Grid item xs={12}>
            <FormGroup>
              <FormControlLabel
                control={
                  <Checkbox
                    checked={audioOnly}
                    onChange={(e) => {
                      setAudioOnly(e.target.checked);
                      if (e.target.checked) {
                        setFormat('mp3');
                      }
                    }}
                    sx={{
                      color: isDarkMode ? 'primary.light' : 'primary.main',
                      '&.Mui-checked': {
                        color: isDarkMode ? 'primary.light' : 'primary.main',
                      },
                    }}
                  />
                }
                label="Apenas áudio"
                sx={{ color: isDarkMode ? 'text.primary' : 'text.secondary' }}
              />
            </FormGroup>
          </Grid>

          {error && (
            <Grid item xs={12}>
              <Alert severity="error">{error}</Alert>
            </Grid>
          )}

          {success && (
            <Grid item xs={12}>
              <Alert severity="success">{success}</Alert>
            </Grid>
          )}

          <Grid item xs={12}>
            <Button
              variant="contained"
              color="primary"
              onClick={handleDownload}
              disabled={loading}
              startIcon={loading ? <CircularProgress size={20} /> : <DownloadIcon />}
              fullWidth
              sx={{
                mt: 2,
                bgcolor: isDarkMode ? 'primary.light' : 'primary.main',
                '&:hover': {
                  bgcolor: isDarkMode ? 'primary.dark' : 'primary.dark',
                },
              }}
            >
              {loading ? 'Baixando...' : 'Baixar'}
            </Button>
          </Grid>
        </Grid>
      </Paper>
    </Box>
  );
};

export default Download;
