import React, { useState } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import { useTheme } from '../context/ThemeContext';
import { DocumentDuplicateIcon } from '@heroicons/react/24/outline';

const NewTranscription = () => {
  const [url, setUrl] = useState('');
  const [title, setTitle] = useState('');
  const [type, setType] = useState('youtube');
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [result, setResult] = useState(null);
  const [progress, setProgress] = useState(0);
  const [status, setStatus] = useState('');
  const { token } = useAuth();
  const { isDarkMode } = useTheme();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setResult(null);
    setProgress(0);
    setStatus('Preparando...');

    try {
      const formData = new FormData();
      formData.append('url', url);
      formData.append('type', type);
      formData.append('title', title);
      if (file) {
        formData.append('file', file);
      }

      setStatus('Baixando arquivo...');
      setProgress(20);

      const response = await axios.post(
        'http://localhost:3000/transcribe',
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'multipart/form-data',
          },
          onUploadProgress: (progressEvent) => {
            const percentCompleted = Math.round((progressEvent.loaded * 100) / progressEvent.total);
            setProgress(20 + (percentCompleted * 0.3)); // 20-50%
            setStatus('Enviando arquivo...');
          },
        }
      );

      setStatus('Processando transcrição...');
      setProgress(50);

      // Simulate processing time
      await new Promise(resolve => setTimeout(resolve, 1000));
      setProgress(100);
      setStatus('Concluído!');

      setResult(response.data);
      setUrl('');
      setTitle('');
      setFile(null);
    } catch (err) {
      console.error('Error:', err);
      setError(err.response?.data?.error || 'Erro ao processar a transcrição');
    } finally {
      setLoading(false);
      setProgress(0);
      setStatus('');
    }
  };

  const copyToClipboard = () => {
    navigator.clipboard.writeText(result.content);
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files?.[0];
    if (selectedFile) {
      setFile(selectedFile);
    }
  };

  const handleTypeChange = (e) => {
    setType(e.target.value);
    setFile(null);
    setUrl('');
  };

  return (
    <div className="max-w-2xl mx-auto">
      <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-6">Nova Transcrição</h2>
      
      <form onSubmit={handleSubmit} className="space-y-6">
        <div>
          <label htmlFor="title" className="block text-sm font-medium text-gray-700 dark:text-gray-300">
            Título da Transcrição
          </label>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className="mt-1 block w-full rounded-md border-gray-300 dark:border-gray-700 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm dark:bg-gray-800 dark:text-white px-4 py-3"
            placeholder="Digite um título para a transcrição"
            required
          />
        </div>

        <div>
          <label htmlFor="type" className="block text-sm font-medium text-gray-700 dark:text-gray-300">
            Tipo de Conteúdo
          </label>
          <select
            id="type"
            value={type}
            onChange={handleTypeChange}
            className="mt-1 block w-full rounded-md border-gray-300 dark:border-gray-700 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm dark:bg-gray-800 dark:text-white px-4 py-3"
          >
            <option value="youtube">YouTube</option>
            <option value="instagram">Instagram</option>
            <option value="video">Vídeo</option>
            <option value="audio">Áudio</option>
          </select>
        </div>

        {(type === 'youtube' || type === 'instagram') ? (
          <div>
            <label htmlFor="url" className="block text-sm font-medium text-gray-700 dark:text-gray-300">
              URL ou Link
            </label>
            <input
              type="text"
              id="url"
              value={url}
              onChange={(e) => setUrl(e.target.value)}
              className="mt-1 block w-full rounded-md border-gray-300 dark:border-gray-700 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm dark:bg-gray-800 dark:text-white px-4 py-3"
              placeholder="Cole o link aqui"
              required
            />
          </div>
        ) : (
          <div>
            <label htmlFor="file" className="block text-sm font-medium text-gray-700 dark:text-gray-300">
              {type === 'video' ? 'Selecione um vídeo' : 'Selecione um áudio'}
            </label>
            <input
              type="file"
              id="file"
              onChange={handleFileChange}
              accept={type === 'video' ? 'video/*' : 'audio/*'}
              className="mt-1 block w-full text-sm text-gray-500 dark:text-gray-400
                file:mr-4 file:py-3 file:px-4
                file:rounded-md file:border-0
                file:text-sm file:font-semibold
                file:bg-indigo-50 file:text-indigo-700
                hover:file:bg-indigo-100
                dark:file:bg-indigo-900 dark:file:text-indigo-200
                dark:hover:file:bg-indigo-800"
              required
            />
          </div>
        )}

        {error && (
          <div className="text-red-500 text-sm">{error}</div>
        )}

        {loading && (
          <div className="space-y-2">
            <div className="w-full bg-gray-200 dark:bg-gray-700 rounded-full h-2.5">
              <div
                className="bg-indigo-600 h-2.5 rounded-full transition-all duration-300"
                style={{ width: `${progress}%` }}
              ></div>
            </div>
            <p className="text-sm text-gray-500 dark:text-gray-400 text-center">{status}</p>
          </div>
        )}

        <button
          type="submit"
          disabled={loading}
          className="w-full flex justify-center py-3 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50"
        >
          {loading ? 'Processando...' : 'Transcrever'}
        </button>
      </form>

      {result && (
        <div className="mt-8 bg-white dark:bg-gray-800 shadow rounded-lg p-6">
          <div className="flex justify-between items-start mb-4">
            <h3 className="text-lg font-medium text-gray-900 dark:text-white">{result.title}</h3>
            <button
              onClick={copyToClipboard}
              className="inline-flex items-center px-3 py-1 border border-transparent text-sm font-medium rounded-md text-indigo-700 dark:text-indigo-200 bg-indigo-100 dark:bg-indigo-900 hover:bg-indigo-200 dark:hover:bg-indigo-800 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
            >
              <DocumentDuplicateIcon className="h-4 w-4 mr-2" />
              Copiar
            </button>
          </div>
          <div className="prose max-w-none max-h-96 overflow-y-auto dark:prose-invert">
            <p className="whitespace-pre-wrap text-gray-900 dark:text-gray-100">{result.content}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default NewTranscription;
