import React, { useState, useEffect } from 'react';
import { useTheme } from '../context/ThemeContext';
import { DocumentCheckIcon, XCircleIcon } from '@heroicons/react/24/outline';

const Settings = () => {
  const [apiToken, setApiToken] = useState('');
  const [saved, setSaved] = useState(false);
  const [error, setError] = useState('');
  const { isDarkMode } = useTheme();

  useEffect(() => {
    // Load token from localStorage when component mounts
    const savedToken = localStorage.getItem('apiToken');
    if (savedToken) {
      setApiToken(savedToken);
    }
  }, []);

  const handleSave = () => {
    try {
      localStorage.setItem('apiToken', apiToken);
      setSaved(true);
      setError('');
      setTimeout(() => setSaved(false), 2000);
    } catch (err) {
      setError('Erro ao salvar o token');
      console.error('Error saving token:', err);
    }
  };

  const handleClear = () => {
    try {
      localStorage.removeItem('apiToken');
      setApiToken('');
      setSaved(true);
      setError('');
      setTimeout(() => setSaved(false), 2000);
    } catch (err) {
      setError('Erro ao limpar o token');
      console.error('Error clearing token:', err);
    }
  };

  return (
    <div className="max-w-2xl mx-auto mt-8">
      <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-8">Configurações</h2>
      
      <div className="bg-white dark:bg-gray-800 shadow rounded-lg p-6">
        <div className="space-y-6">
          <div>
            <label htmlFor="apiToken" className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
              Token da API
            </label>
            <div className="mt-1 relative rounded-md shadow-sm">
              <input
                type="text"
                id="apiToken"
                value={apiToken}
                onChange={(e) => setApiToken(e.target.value)}
                className="block w-full rounded-md border-gray-300 dark:border-gray-600 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm dark:bg-gray-700 dark:text-white px-4 py-3"
                placeholder="Cole seu token aqui"
              />
            </div>
            <p className="mt-2 text-sm text-gray-500 dark:text-gray-400">
              Este token será usado para autenticar suas requisições à API.
            </p>
          </div>

          <div className="flex space-x-4">
            <button
              onClick={handleSave}
              className="inline-flex items-center px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
            >
              <DocumentCheckIcon className="h-5 w-5 mr-2" />
              Salvar Token
            </button>
            <button
              onClick={handleClear}
              className="inline-flex items-center px-4 py-2 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-red-600 hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
            >
              <XCircleIcon className="h-5 w-5 mr-2" />
              Limpar Token
            </button>
          </div>

          {saved && (
            <div className="text-green-500 dark:text-green-400 text-sm">
              Configurações salvas com sucesso!
            </div>
          )}

          {error && (
            <div className="text-red-500 text-sm">
              {error}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Settings;
