import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import { useTheme } from '../context/ThemeContext';
import { DocumentDuplicateIcon, TrashIcon, XMarkIcon } from '@heroicons/react/24/outline';

const History = () => {
  const [transcriptions, setTranscriptions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [selectedTranscription, setSelectedTranscription] = useState(null);
  const { token } = useAuth();
  const { isDarkMode } = useTheme();

  useEffect(() => {
    fetchTranscriptions();
  }, []);

  const fetchTranscriptions = async () => {
    try {
      const response = await axios.get('http://localhost:3000/transcriptions', {
        headers: { Authorization: `Bearer ${token}` }
      });
      console.log('Transcriptions:', response.data); // Debug log
      setTranscriptions(response.data);
    } catch (err) {
      console.error('Fetch error:', err); // Debug log
      setError('Erro ao carregar histórico');
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (transcription) => {
    console.log('Deleting transcription:', transcription); // Debug log
    if (window.confirm('Tem certeza que deseja excluir esta transcrição?')) {
      try {
        await axios.delete(`http://localhost:3000/transcriptions/${transcription.id}`, {
          headers: { Authorization: `Bearer ${token}` }
        });
        setTranscriptions(transcriptions.filter(t => t.id !== transcription.id));
        if (selectedTranscription?.id === transcription.id) {
          setSelectedTranscription(null);
        }
      } catch (err) {
        console.error('Delete error:', err); // Debug log
        setError('Erro ao excluir transcrição');
      }
    }
  };

  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-indigo-600"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-red-500 text-center p-4">
        {error}
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto mt-8">
      <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-8">Transcrições Salvas</h2>
      
      {transcriptions.length === 0 ? (
        <div className="text-center py-12">
          <p className="text-gray-500 dark:text-gray-400">Nenhuma transcrição encontrada</p>
        </div>
      ) : (
        <div className="space-y-4">
          {transcriptions.map((transcription) => (
            <div
              key={transcription.id}
              className="bg-white dark:bg-gray-800 shadow rounded-lg overflow-hidden cursor-pointer hover:shadow-lg transition-shadow"
              onClick={() => setSelectedTranscription(transcription)}
            >
              <div className="p-6">
                <div className="flex justify-between items-start">
                  <div>
                    <h3 className="text-lg font-medium text-gray-900 dark:text-white">
                      {transcription.title}
                    </h3>
                    <p className="text-sm text-gray-500 dark:text-gray-400 mt-1">
                      Criado em: {new Date(transcription.createdAt).toLocaleString()}
                    </p>
                  </div>
                  <div className="flex space-x-2">
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        handleDelete(transcription);
                      }}
                      className="inline-flex items-center px-3 py-1 border border-transparent text-sm font-medium rounded-md text-red-700 dark:text-red-200 bg-red-100 dark:bg-red-900 hover:bg-red-200 dark:hover:bg-red-800 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
                    >
                      <TrashIcon className="h-4 w-4 mr-2" />
                      Excluir
                    </button>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Modal */}
      {selectedTranscription && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white dark:bg-gray-800 rounded-lg max-w-3xl w-full max-h-[80vh] overflow-hidden">
            <div className="p-6">
              <div className="flex justify-between items-start mb-4">
                <h3 className="text-xl font-medium text-gray-900 dark:text-white">
                  {selectedTranscription.title}
                </h3>
                <div className="flex space-x-2">
                  <button
                    onClick={() => copyToClipboard(selectedTranscription.content)}
                    className="inline-flex items-center px-3 py-1 border border-transparent text-sm font-medium rounded-md text-indigo-700 dark:text-indigo-200 bg-indigo-100 dark:bg-indigo-900 hover:bg-indigo-200 dark:hover:bg-indigo-800 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                  >
                    <DocumentDuplicateIcon className="h-4 w-4 mr-2" />
                    Copiar
                  </button>
                  <button
                    onClick={() => setSelectedTranscription(null)}
                    className="inline-flex items-center px-2 py-1 border border-transparent text-sm font-medium rounded-md text-gray-700 dark:text-gray-200 hover:bg-gray-100 dark:hover:bg-gray-700 focus:outline-none"
                  >
                    <XMarkIcon className="h-5 w-5" />
                  </button>
                </div>
              </div>
              <div className="prose prose-sm max-w-none dark:prose-invert overflow-y-auto" style={{ maxHeight: 'calc(80vh - 200px)' }}>
                <p className="whitespace-pre-wrap text-gray-900 dark:text-gray-100">
                  {selectedTranscription.content}
                </p>
              </div>
              <div className="mt-4 text-sm text-gray-500 dark:text-gray-400">
                Criado em: {new Date(selectedTranscription.createdAt).toLocaleString()}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default History;
